export default function NotificationBox(){

    const [selectedItem, setSelectedItem] = useState(0) 

    function handleClick(index){
        setSelectedItem(index)
        console.log(selectedItem)
    }


    const item = ["All", "Team", "Message"]

    return (<>
            <div className="w-full h-72 bg-white p-2 absolute top-16 z-20 rounded-xl shadow-xl shadow-gray-300 ">
                <div className="w-full h-10 bg-gray-300 rounded-xl border border-slate-400">
                    <ul className="w-full h-full items-center justify-between font-medium flex flex-row relative">

                       {item.map((itemName, index) => (
                         <li className={`w-max h-full  px-2 rounded-lg flex items-center ${ selectedItem == index ? 'text-white bg-black' :'text-black' } cursor-pointer transition-all du`} key={index} onClick={() => handleClick(index)}>{itemName}</li>
                       ))}

                    </ul>
                </div>
                <ul className="">

                </ul>
            </div>
    </>)
}
