import { faXRay, faXmark } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { motion } from "framer-motion";
import React, { MouseEventHandler, useState } from "react";


type propModal = {
    open: boolean
    close: MouseEventHandler
    task: boolean
    // name: string,
    // debut: DateConstructor | null
    // fin: DateConstructor | null
}


export default function ModalProject(open: propModal){

        const [value, setValue] =  useState({
            nameProjet : "",
            dtDebut: "",
            dtFin: ""
        })

            const setNameProjet = (propertyname, Newvalue) => {
                setValue({...value, [propertyname]: value })
            }

            const {nameProjet, dtDebut, dtFin} = value;
    return (<>
        {open.open && (
        <motion.div 
        initial={{opacity: 0}}
        animate={{opacity: 1}}
        transition={{duration:0.2, type: 'tween', bounce: 0.2 }}


        className="w-full h-screen bg-white/5 backdrop-blur-xl absolute flex justify-center items-center z-30">
            <div className="w-modal rounded-xl h-modal  bg-white/65 z-20 top-52 left-1 overflow-hidden flex flex-row" >
                    <form action="" className={`w-${open.task ? '1/2' : 'full'} h-full  flex flex-col justify-start gap-3 px-2  border`} >
                        <div className="w-full p-2 flex flex-row justify-start relative">
                            <h5 className="laila-light">Projet - <span className="">{nameProjet === '' ? '': nameProjet}</span> </h5>
                          <span className="absolute right-2 laila-semibold bg-red-500 text-white px-2 py-1 rounded-lg flex flex-row justify-center items-center cursor-pointer" onClick={open.close}><FontAwesomeIcon icon={faXmark}  className="rounded-2xl border border-white w-5 h-5 mr-1"/> Annuler </span>  
                        </div>

                        <div className="w-full h-full flex flex-row justify-between">
                            <div className="w-1/2 h-full flex justify-start gap-10 flex-col  p-2">
                            <label htmlFor="name" className="flex flex-col items-start w-full">
                                <span className="laila-semibold">Nom : <span className="text-red-400 text-lg font-bold">*</span> </span>
                                <input type="text" name="name" value={nameProjet} id="" placeholder="entrer le nom du projet" onChange={setNameProjet } className="rounded-lg w-full laila-light"/>
                            </label>

                                <div className="w-full flex flex-row gap-10">
                                    <label htmlFor="dtdebut" className="w-1/2 flex items-start flex-col">
                                    <span className="laila-semibold">DÃ©but : <span className="text-red-400 text-lg font-bold">*</span> </span>
                                        <input type="date" name="dtdebut" value={dtDebut} onChange={set} className="w-full rounded-lg laila-light" />
                                    </label>
                                    <label htmlFor="dtfin" className="w-1/2 flex items-start flex-col">
                                    <span className="laila-semibold">Fin: <span className="text-red-400 text-lg font-bold">*</span> </span>
                                        <input type="date" name="dtfin" className="rounded-lg w-full laila-light" />
                                    </label>
                                </div>

                            </div>
                            <div className="w-1/2 h-full bg-blue-300"></div>
                        </div>
                    </form>
                    {/* <form action="" className="w-1/2 h-full  " >

                    </form> */}

                 </div>
        </motion.div>


        )}
    </>

    )
}
