import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAdd, faMinus } from "@fortawesome/free-solid-svg-icons";
import React, { useRef, useState } from "react";
import { IconProp } from "@fortawesome/fontawesome-svg-core";

type Menu = {
        name: String;
        icon: IconProp
}


export default function MenuDashboard(menu: Menu, submenu: string[]){
    const boxsub = useRef(null);
    const [isOpen, setOpen] = useState(false)

     async function  handleOpenSubMenu () {
      await  setOpen(!isOpen);
        if (boxsub.current) {
           boxsub.current.style.display = isOpen == false ? 'none' : 'block' 
        }
    }



    return (<>
            <li className="p-3 mx-4  rounded-lg flex flex-col gap-3 items-center justify-start text-slate-500 relative">
                    <div className="w-full flex flex-row justify-start items-center p-2" onClick={handleOpenSubMenu} >
                    <FontAwesomeIcon icon={menu.icon} className="" size="lg" />
                    <span className="pl-3 font-bold">{menu.name}</span>
                    <FontAwesomeIcon icon={isOpen ? faAdd : faMinus} className="bg-slate-300 p-1 w-3 h-3 text-slate-700 rounded-full absolute right-0 transition-all"/>
                    </div>
                    <div className="w-full rounded-es-lg p-2 border-l border-slate-400 transition-all" ref={boxsub}  style={{display: 'none'}}>
                        <ul className="flex flex-col justify-start items-start gap-2 text-sm">

                        </ul>
                    </div>
                </li>

    </>)    
}
