import React, { useState } from "react";
import MenuDashboard from "../Component/MenuDashboard.tsx";
import { faHouse, faFolderTree, faListCheck } from "@fortawesome/free-solid-svg-icons";

export default function Project(){
    const [active, setActive] = useState({
        activeHome: true,
        activeProject: false,
        activeTask: false 
    });
   function PageHome (){
    setActive({
        activeHome: true,
        activeProject: false,
        activeTask: false
    })
   }
   function PageProject (){
    setActive({
        activeHome: false,
        activeProject: true,
        activeTask: false
    })

   }
   function PageTask (){
    setActive({
        activeHome: false,
        activeProject: false,
        activeTask: true
    })
    return(<>
            <div className="w-full h-screen bg-red-500 flex flex-row justify-between">
            <div className="w-80 h-screen bg-blue-400 border-r-2 border-slate-300 overflow-hidden">
                    <header className="p-7 font-popone m-2">
                        <p className="text-2xl font-bold">TaskManager</p>
                    </header>
                    <main className="mt-12 overflow-y-auto h-full">
                        <ul className="flex flex-col gap-5">
                          <MenuDashboard name={'Home'} icon={faHouse} isSbumenu={false} subMenu={null} active={active.activeHome} onItem={PageHome} />
                            <MenuDashboard name={'Project'} icon={faFolderTree} isSbumenu={true} subMenu={null} active={active.activeProject} onItem={PageProject}  />
                            <MenuDashboard name={'Task'} icon={faListCheck} subMenu={null} isSbumenu={true} active= {active.activeTask} onItem={PageTask} />
                        </ul>
                    </main>
                </div>
            </div>

    </>)
}
}
