import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHouse, faFolderTree, faGear, faListCheck, faBell, faCirclePlus} from "@fortawesome/free-solid-svg-icons";
import { useRef, useState } from "react";
import React from "react";
import MenuDashboard from "../Component/MenuDashboard.tsx";
import TopMenu from "../Component/menubar.tsx";
import CardProject from "../Component/CardProject.tsx";
import { useScroll } from "framer-motion";
import { act } from "react-dom/test-utils/index";
import User from '../../Model/user.ts'
import { useLocation } from 'react-router-dom';
export default function Home(){
    const location = useLocation();
    const state = location.state

    console.log({
        id: state.id,
        lastname: state.lastname,
        firstname: state.firstname,
    })


    const [active, setActive] = useState({
        activeHome: true,
        activeProject: false,
        activeTask: false 
    });
   function PageHome (){
    setActive({
        activeHome: true,
        activeProject: false,
        activeTask: false
    })
   }
   function PageProject (){
    setActive({
        activeHome: false,
        activeProject: true,
        activeTask: false
    })

   }
   function PageTask (){
    setActive({
        activeHome: false,
        activeProject: false,
        activeTask: true
    })
   }



    return (<>

            <div className="w-full h-screen bg-white flex flex-row">
                <div className="w-80 h-screen bg-white border-r-2 border-slate-300 overflow-hidden">
                    <header className="p-7 font-popone m-2">
                        <p className="text-2xl font-bold">TaskManager</p>
                    </header>
                    <main className="mt-12 overflow-y-scroll h-full">
                        <ul className="flex flex-col gap-5">
                          <MenuDashboard name={'Home'} icon={faHouse} isSbumenu={false} subMenu={null} active={active.activeHome} onItem={PageHome} />
                            <MenuDashboard name={'projet'} icon={faFolderTree} isSbumenu={true} subMenu={null} active={active.activeProject} onItem={PageProject}  />
                            <MenuDashboard name={'Tâche'} icon={faListCheck} subMenu={null} isSbumenu={true} active= {active.activeTask} onItem={PageTask} />
                        </ul>
                    </main>
                </div>
                <div className="bg-slate-200 w-full relative">
                    <div className="w-max h-12 p-2 bg-white  absolute right-0 mt-2 mr-2 rounded-full flex justify-between items-center ">
                        <ul className="flex flex-row gap-2">
                            <TopMenu name="créer un projet" icon={faCirclePlus} />
                            <TopMenu name="Notification" icon={faBell} />
                            <TopMenu name="Paramètre" icon={faGear} />
                        </ul>
                        <img src="https://img.freepik.com/photos-gratuite/portrait-homme-s-amusant_23-2149443790.jpg" className="w-10 h-10 object-cover rounded-full relative p-1" alt="" />
                    </div>
                    <div className="w-full flex flex-row justify-start p-5 mt-24 gap-2">
                    <CardProject name="Leadin Page" time="" />
                    <CardProject name="DashBoard Admin" time="" />
                    <CardProject name="Api E-commerce" time="" />
                    </div>
                </div>
            </div>


        </>)
}
