import React, { useState } from "react";

export default function NotificationBox(){

        const [position, setPosition] = useState({
            x: 0,
            y:0
        })

    const ChangeMenu = (e) => {
        const parentRect = e.currentTarget.getBoundingClientRect();
        const x = e.clientX;
        const y = e.clientY - parentRect.top;
        setPosition({ x, y });

    }
    const {x,y} = position;

    return (<>
            <div className="w-full h-72 bg-white p-2 absolute top-16 z-20 rounded-xl shadow-xl shadow-gray-300 ">
                <div className="w-full h-10 bg-gray-300 rounded-xl">
                    <ul className="w-full h-full items-center justify-between bg-red-400 font-medium flex flex-row relative">
                        <div className="w-24 h-10 bg-black absolute -z-10" style={ {transform: `translateX(${x}px) translateY(${y}px)`} }></div>
                        <li className="w-max h-full px-4 rounded-lg bg-black text-white flex items-center" onClick={ChangeMenu}>
                                All
                        </li>
                        <li className="text-center px-4" onClick={ChangeMenu}>
                            Team
                        </li>
                        <li className="text-center px-4" onClick={ChangeMenu}>
                            Message
                        </li>
                    </ul>
                </div>
            </div>
    </>)
}
